import React, { useEffect, useState } from "react";

function SearchInfo(props) {
  const [moviess, setMoviess] = useState([]);
  const [searchMoviee, setSearchMovie] = useState("");
  const searchMovie = async (searchName) => {
    const url = `https://api.themoviedb.org/3/search/movie?api_key=1469c0124feb2071e2822b7f4023b656&query=${searchName}&page=1`;
    const response = await fetch(url);
    const responseJSON = await response.json();

    if (responseJSON.results) {
      setMoviess(responseJSON.results);
    }
  };
  useEffect(() => {
    searchMovie(searchMoviee);
  }, [searchMoviee]);

  return (
    <div>
      <div className="searchInfo">
        <div className="searchBar">
          <input
            type="search"
            value={props.value}
            // onSubmit={(e) => setSearchMovie(e.target.value)}
            placeholder="Search for a Movie"
            onChange={(event) => props.setSearchMovie(event.target.value)}
          ></input>
        </div>
      </div>
      <div className="info">
        <p>
          Movie streamz is a movie app
          blablablablablablaablabalbalbalablblablbalabblablalblablablablablablablalna
        </p>
      </div>
      <div className="searchMovieee">

      </div>
    </div>
  );
}
export default SearchInfo;
