  const searchMovie = async (movie) => {
      const url = "https://api.themoviedb.org/3/search/movie?api_key=1469c0124feb2071e2822b7f4023b656&query=${movie}"
        const response = await fetch(url);
        const resJSON = await response.json();
        if(resJSON.results){
            setMovies(resJSON.results);
        }
  }
  useEffect(() => {
      searchMovie(search)
  }, [search]);

    return(
        <div>
