import React from "react";

class Movies extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false,
    };
  }
  moviesFetching() {
    fetch(
      `https://api.themoviedb.org/3/movie/popular?api_key=1469c0124feb2071e2822b7f4023b656&language=en-US&page=1`
    )
    .then((data) => console.log(data.json()))
      .then((res) => console.log(res.json()))
      .then((json) => {this.setState({ items: json, DataisLoaded: true });});
    }
      render() {
          const { DataisLoaded, items } = this.state;
          if(!DataisLoaded) return <h1>Data is not loaded yet</h1>;
          else return (
              <div className="Movies_section">
        <h1>Fetching data</h1>
        {items.map((item) => {
            <div className="movies">
            <h3 key={item.results[6]}>{item.results[6]}</h3>
            <img key={item.backdrop_path}>{item.backdrop_path}</img>
            <p key={item.overview}>{item.overview}</p>
            <p key={item.release_date}>Release Date: {item.release_date} </p>
          </div>;
        })}
      </div>
    );
}
}

export default Movies;
