import Header from "./components/header/Header";
import Movies from "./components/movies/Movies";
import SearchBarInfo from './components/searchBarAndInfo/SearchBarInfo'
import { useState, useEffect } from "react";
function App() {
  const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [results, setResults] = useState([]);

    useEffect(() => {
        fetch(
            "https://api.themoviedb.org/3/movie/popular?api_key=1469c0124feb2071e2822b7f4023b656&language=en-US&page=1"
            )
            .then((res) => res.json())
            .then((res) => {
                setResults(res);
                setIsLoaded(true);
            },

            (error) => {
                setIsLoaded(true);
                setError(error)
            }
            )
    }, [])
  return <div className="App">
    <Header />
    <SearchBarInfo />
    <div>
    {results?.data.results.map(item => (
                <div className="movies">
                <h3 key={item.original_title}>{item.original_title}</h3>
                </div>
            ))}

    </div>
    {/* <Movies /> */}


  </div>;
}

export default App;
