import React, { useEffect, useState } from 'react';


function SearchInfo () {
  const [movies, setMovies] = useState([]);
  const [search, setSearch] = useState('');



  const searchMovie = async (movie) => {
      const url = `https://api.themoviedb.org/3/search/movie?api_key=1469c0124feb2071e2822b7f4023b656&query=${movie}`;
        const response = await fetch(url);
        const resJSON = await response.json();
        console.log(resJSON);
        if(resJSON.results){
            setMovies(resJSON.results);
        }
  };
  useEffect(() => {
      searchMovie(search)
  }, [search]);

    return(
        <div>
        <div className= "searchInfo">
            <div className="searchBar">
                <input type="search" search={search} setSearch={setSearch} placeholder="Search for a Movie" />
                <button onClick={setSearch(search)}>Search</button>
            </div>
        </div>
            <div className="info">
                <p>Movie streamz is a movie app blablablablablablaablabalbalbalablblablbalabblablalblablablablablablablalna</p>
            </div>
            <div className="searchMovieee">
            <h1>{movies.results}</h1>
        </div>
            </div>
    )
}
export default SearchInfo;
