import Header from "./components/header/Header";
import Movies from "./components/movies/Movies";
import SearchBarInfo from './components/searchBarAndInfo/SearchBarInfo'
import { useState, useEffect } from "react";
function App() {
  const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [results, setResults] = useState([]);

    useEffect(() => {
        fetch(
            "https://api.themoviedb.org/3/movie/popular?api_key=1469c0124feb2071e2822b7f4023b656&language=en-US&page=1"
            ).then(console.log(fetch('https://api.themoviedb.org/3/movie/popular?api_key=1469c0124feb2071e2822b7f4023b656&language=en-US&page=1')))
            .then((res) => res.json())
            .then((res) => {
                setIsLoaded(true);
                setResults(res);
            },
            (error) => {
                setIsLoaded(true);
                setError(error)
            }
            )
    }, [])

    if (error) {
        return <div>Error: {error.message}</div> 
    } else if (!isLoaded) {
        return <div>Loading...</div>;
    } else {
        return (
            <div className="Movies_section">
            <h1>Fetching data</h1>
            <div >
            {results.map(item => (
                <div className="movies">
                <h3 key={item.original_title}>{item.original_title}</h3>

                {/* <img key={item.backdrop_path}>{item.backdrop_path}</img>
                <p key={item.overview}>{item.overview}</p>
                <p key={item.release_date}>Release Date: {item.release_date} </p> */}
                </div>
            ))}
            </div>
          </div>
        )
    }
  return <div className="App">
    <Header />
    <SearchBarInfo />
    <Movies />


  </div>;
}

export default App;
